import { Link, useLocation } from 'react-router-dom';
import { Disclosure } from '@headlessui/react'

import data from './data';
import { useToggle } from '../provider/context';

const style = {
  title: `mx-4 text-sm`,
  inactive: `text-black`,
  active: `font-medium text-green-400 hover:text-green-400`,
  link: `flex items-center justify-start my-2 p-4 w-full hover:text-white`,
  close: `lg:duration-700 lg:ease-out lg:invisible lg:opacity-0 lg:transition-all`,
  open: `lg:duration-500 lg:ease-in lg:h-auto lg:opacity-100 lg:transition-all lg:w-auto`,
};

export default function SidenavItems() {
  const { pathname } = useLocation();
  const { open, toggle } = useToggle();
  return (
    <ul className="md:pl-5">
      <li>
        {data.map((item) => (
          <Link to={item.link} key={item.title}>
            <span
              className={`${style.link} 
            ${item.link === pathname ? style.active : style.inactive}`}
            >
              <span>{item.icon}</span>
              <span
                className={`${style.title} ${open ? style.open : style.close}`}
              >
                {item.title}
              </span>
            </span>
          </Link>
        ))}
      </li>
      <li
      >
        <Disclosure >
          <Disclosure.Button className={`${style.link}`}>
            <span className="text-black" onClick={toggle}>aa</span>
            <span
              className={`${style.title} ${open ? style.open : style.close} text-black`}
            > Documentation
            </span>
          </Disclosure.Button>
          <Disclosure.Panel className={` ${style.title} ${open ? style.open : style.close} rounded-lg bg-gray-200 -mt-4 w-12  mr-14 p-2`}>
            <div>
              <Link className="p-2 m-2 text-gray-900 ">SKD</Link>
              <Link className="p-2 m-2 text-gray-900 ">SKD</Link>
            </div>

          </Disclosure.Panel>
        </Disclosure>
      </li>
    </ul>
  );
}
